class Aircraft extends BaseAircraft
{

    /**
     * Get all aircraft with his types
     * @param $filter array
     * @return array
     */
    public static function findAll($filter = array())
    {
        $r = Doctrine_Query::create()
                    ->from("Aircraft a")
                    ->leftJoin("a.AircraftType t")
                    ->leftJoin("a.AircraftStatus s");

        if (isset($filter['exclude']) && !empty($filter['exclude']))
            $r->andWhere('a.id not in ('. implode(",", $filter['exclude']) .')');
        
        return $r->fetchArray(true);
    }

    /**
     * Create a aircraft
     * @param array $params
     * @return mixed
     */
    public static function create($params)
    {
        if(!empty($params))
        {
            $aircraft = new Aircraft();
            $aircraft->name = $params['name'];
            $aircraft->type_id = $params['type_id'];
            $aircraft->status_id = $params['status_id'];
            $aircraft->save();
            $aircraft->refresh();
            
            return $aircraft->id;
        }
        
        return false;
    }

    /**
     * Return all aircraft by array of ids
     * @param array $aircrafts
     * @return mixed
     */
    public static function findIn($aircrafts, $inArray = true)
    {
        if (!empty($aircrafts))
        {
            $r = Doctrine_Query::create()
                    ->from("Aircraft a")
                    ->addWhere("a.id in (".implode(",", $aircrafts).")");
                    
            if ($inArray)
                $r->fetchArray(true);
            else
                $r->execute();
            return $r;
        }

        return null;
    }

    /**
     * Return a array from a aircraft id
     * @param int $aircraftId
     * @return array
     */
    public static function findById($aircraftId)
    {
        return Doctrine_Query::create()
                    ->from("Aircraft a")
                    ->leftJoin("a.AircraftStatus s")
                    ->leftJoin("a.AircraftType t")
                    ->addWhere("a.id = $aircraftId")
                    ->fetchOne()
                    ->toArray(true);
    }

    /**
     * Delete the aircraft for a user
     * @param int $userId
     * @param array $aircrafts
     * @return mixed
     */
    public static function deleteUserAircrafts($userId, $aircrafts)
    {
        if (!empty($aircrafts) && !empty($userId))
        {
            $r = Doctrine_Query::create()
                        ->delete("UserAircraft ua")
                        ->addWhere("ua.user_id = $userId")
                        ->addWhere("ua.aircraft_id in (".implode(",", $aircrafts).")")
                        ->execute();

            return $r;
        }
        return false;
    }

    /**
     * Edit a aircraft
     * @param array $params
     * @return mixed
     */
    public static function edit($params)
    {
        if (!empty($params))
        {
            $aircraft = Doctrine::getTable("Aircraft")->findOneById($params['aircraft_id']);
            $aircraft->id = $params['aircraft_id'];
            $aircraft->type_id = $params['type_id'];
            $aircraft->status_id = $params['status_id'];

            $aircraft->save();

            return $aircraft->id;
        }
        return false;
    }
}